    int partition(std::vector<T>& a, int lo, int hi) {
        int i = lo;
        int j = hi + 1;

        while(true) {
            // process i pointer
            // find item on left to swap
            while(a[++i] < a[lo]) {
                if (i == hi) {
                    break;
                }
